import React from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import {STLLoader} from 'three/examples/jsm/loaders/STLLoader';

const RubberDuck = require("./../../assets/cad-duck.stp")

class Render extends React.Component {

  componentDidMount() {
        var container = document.createElement( 'div' );
        document.body.appendChild( container );

        var camera = new THREE.PerspectiveCamera( 35, window.innerWidth / window.innerHeight, 1, 15 );
        camera.position.set( 3, 0.15, 3 );

        var cameraTarget = new THREE.Vector3( 0, - 0.25, 0 );

        var scene = new THREE.Scene();
        scene.background = new THREE.Color( 0x72645b );
        scene.fog = new THREE.Fog( 0x72645b, 2, 15 );


        // Ground

        var plane = new THREE.Mesh(
          new THREE.PlaneBufferGeometry( 40, 40 ),
          new THREE.MeshPhongMaterial( { color: 0x999999, specular: 0x101010 } )
        );
        plane.rotation.x = - Math.PI / 2;
        plane.position.y = - 0.5;
        scene.add( plane );

        plane.receiveShadow = true;
        var loader = new STLLoader();
        loader.load( './models/stl/ascii/slotted_disk.stl', function ( geometry ) {

          var material = new THREE.MeshPhongMaterial( { color: 0xff5533, specular: 0x111111, shininess: 200 } );
          var mesh = new THREE.Mesh( geometry, material );

          mesh.position.set( 0, - 0.25, 0.6 );
          mesh.rotation.set( 0, - Math.PI / 2, 0 );
          mesh.scale.set( 0.5, 0.5, 0.5 );

          mesh.castShadow = true;
          mesh.receiveShadow = true;

          scene.add( mesh );

        } );

        var renderer = new THREE.WebGLRenderer( { antialias: true } );
				renderer.setPixelRatio( window.devicePixelRatio );
				renderer.setSize( window.innerWidth, window.innerHeight );
				renderer.outputEncoding = THREE.sRGBEncoding;

				renderer.shadowMap.enabled = true;

				container.appendChild( renderer.domElement );



    var loader = new STLLoader();
    var geometry = loader.load(RubberDuck, );
    var material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
    var mesh = new THREE.Mesh( geometry, material );
    scene.add(mesh);
    camera.position.z = 5;
    var animate = function () {
      requestAnimationFrame( animate );
      mesh.rotation.x += 0.01;
      mesh.rotation.y += 0.01;
      renderer.render( scene, camera );
    };
    animate();
  }

  render() {
    return (
      <div ref={ref => (this.mount = ref)} />
    )
  }
}
export default Render
