import React from 'react';
import './Volunteer.css';
import MapContainer from "../MapContainer/MapContainer";
import axios from 'axios';

class Volunteer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            draft: 0.0,
            capacity: 0,
            latitude: 0.0,
            longitude: 0.0,
            phoneNumber: '',
            googleKey: '',
            keyIsLoading: true,
            gpxData: [],
            dataIsLoading: true,
            formErrors: { draft: "", capacity: "", phoneNumber: "", longitude: "", latitude: ""},
        };
    }

    componentDidMount() {
        axios.get('/getKey')
            .then((res) => {
                const key = res.data;
                this.setState({
                    googleKey: key
                }, () => {
                    this.setState({
                        keyIsLoading: false
                    })
                })
            });
    }

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({
            [name]: value
        }, () => {
            console.log(name, "=", value);
        })
        e.preventDefault();
        let formErrors = { ...this.state.formErrors };

        switch (name) {
            case "name":
                formErrors.name = value.length < 3 ? "minimum 3 characters required" : "";
            break;
            case "partySize":
                formErrors.partySize = partySizeRegex.test(value) ? "" : "not a valid number of people";
            break;
            case "phone":
                formErrors.phone = phoneRegex.test(value) ? "" : "invalid phone number";
            break;
            case "longitude":
                formErrors.longitude = longitudeRegex.test(value) ? "" : "invalid longitude";
            break;
            case "latitude":
                formErrors.latitude = latitudeRegex.test(value) ? "" : "invalid latitude";
            break;
            default:
            break;
        }
        this.setState({ formErrors, [name]: value });
    };

    onSubmit = () => {
        axios.get(`/api/getGPX?phone=${this.state.phoneNumber}`)
            .then((res) => {
                console.log(res.data);
                this.setState({
                    gpxData: res.data.tracks[0].segments[0]
                }, () => {
                    this.setState({
                        dataIsLoading: false
                    })
                })
            })
    };

    render() {
        return(
          <div className="volunteer-page parallax">
              <div className="row" style={{height:"100vh"}}>
                  <div className="flex-column-40 col-volunteer-left">
                      <div className="volunteer-title">
                        Volunteer
                      </div>
                      <div className="volunteer-form">
                          <div className="variable">
                              <label>Phone Number</label>
                              <input
                                  placeholder="(---) --- ---"
                                  type="text"
                                  name="phoneNumber"
                                  onChange={this.handleChange}
                              />
                          </div>
                          <div className="variable">
                              <label>Draft (meters)</label>

                              <input
                                  placeholder="0.0 m"
                                  type="text"
                                  name="draft"
                                  onChange={this.handleChange}
                              />
                          </div>
                          <div className="variable">
                              <label>Max. Boat Capacity</label>
                              <input
                                  placeholder="0-20"
                                  type="text"
                                  name="capacity"
                                  onChange={this.handleChange}
                              />
                          </div>
                          <div className="variable">
                              <label>Boat Starting Latitude</label>
                              <input
                                  placeholder="0.000000"
                                  type="text"
                                  name="latitude"
                                  onChange={this.handleChange}
                              />
                          </div>
                          <div className="variable">
                              <label>Boat Starting Longitude</label>
                              <input
                                  placeholder="0.000000"
                                  type="text"
                                  name="longitude"
                                  onChange={this.handleChange}
                              />
                          </div>
                          <div className="request" onClick={() => this.onSubmit()}>
                              Request Job
                          </div>
                      </div>
                  </div>
                  <div className="flex-column-60 col-volunteer-right">
                      {
                        !this.state.keyIsLoading && !this.state.dataIsLoading ?
                        <MapContainer
                            googleKey={this.state.googleKey}
                            geoData={this.state.gpxData}
                            id="google-map"
                        />
                        : null
                      }
                  </div>
              </div>
          </div>
        )
    }
}

export default Volunteer;
